import React from 'react';

export default function EmailButton(props) {
    // Arrow function to copy email to clipboard
    const copyEmailToClipboard = () => {
        // Create a new text area element
        const el = document.createElement('textarea');
        // Set the value of the text area to the email address
        el.value = "hello@commoncents.org";
        // Add the text area to the document
        document.body.appendChild(el);
        // Select the content of the text area
        el.select();
        // Copy the selected text to the clipboard
        document.execCommand('copy');
        // Optional: alert the user that the email has been copied
        alert('Email has been copied to clipboard!');
    };

    return (
        // Render a button that when clicked, will copy the email to the clipboard
        <button onClick={copyEmailToClipboard}> { props.email }</button>
    );
}
