import React from 'react';

export default function EmailCopyButton(props) {
    // Arrow function to copy email to clipboard using the Clipboard API
    const copyEmailToClipboard = async () => {
        try {
            const email = props.email;
            // Use the Clipboard API to copy the email address
            await navigator.clipboard.writeText({ email });
            // Notify the user that the email has been copied
            alert('Email has been copied to clipboard!');
        } catch (err) {
            // Handle any errors (e.g., Clipboard API not available)
            console.error('Failed to copy email: ', err);
            alert('Failed to copy email');
        }
    };

    return (
        // Render a button that when clicked, will copy the email to the clipboard
        <button onClick={copyEmailToClipboard}>Copy Email</button>
    );
}
